import React, { useState } from 'react';

function Table() {

    const [nurse, setNurse] = useState([
        { id: 1, name: 'Jean Dupont', email: 'jean.dupont@example.com', phone: '0123456789' },
        { id: 2, name: 'Marie Curie', email: 'marie.curie@example.com', phone: '0987654321' },
    ]);

    // État pour stocker les valeurs des champs de formulaire
    const [newNurse, setNewNurse] = useState({ name: '', email: '', phone: '' });

    // Fonction pour gérer l'ajout de nouveaux clients
    const handleAddClient = () => {
        const clientToAdd = {
            id: clients.length + 1,
            name: newClient.name,
            email: newClient.email,
            phone: newClient.phone,
        };
        setClients([...clients, clientToAdd]);
        setNewClient({ name: '', email: '', phone: '' });
    };

    // Fonction pour gérer les changements dans le formulaire
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewClient({ ...newClient, [name]: value });
    };

    return (
        <div>
            <h1>Gestion de la base de clients</h1>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nom</th>
                        <th>Email</th>
                        <th>Téléphone</th>
                    </tr>
                </thead>
                <tbody>
                    {clients.map((client) => (
                        <tr key={client.id}>
                            <td>{client.id}</td>
                            <td>{client.name}</td>
                            <td>{client.email}</td>
                            <td>{client.phone}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Ajouter un nouveau client</h2>
            <div>
                <input
                    type="text"
                    name="name"
                    placeholder="Nom"
                    value={newClient.name}
                    onChange={handleInputChange}
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={newClient.email}
                    onChange={handleInputChange}
                />
                <input
                    type="text"
                    name="phone"
                    placeholder="Téléphone"
                    value={newClient.phone}
                    onChange={handleInputChange}
                />
                <button onClick={handleAddClient}>Ajouter</button>
            </div>
        </div>
    );
}

export default Table;
