import React, { useState } from 'react';

function Table() {

    const [nurse, setNurse] = useState([
        { id: 1, name: 'Jean Dupont', email: 'jean.dupont@example.com', phone: '0123456789' },
        { id: 2, name: 'Marie Curie', email: 'marie.curie@example.com', phone: '0987654321' },
    ]);

    // État pour stocker les valeurs des champs de formulaire
    const [newNurse, setNewNurse] = useState({ name: '', email: '', phone: '' });

    // Fonction pour gérer l'ajout de nouveaux nurses
    const handleAddnurse = () => {
        const nurseToAdd = {
            id: nurses.length + 1,
            name: newNurse.name,
            email: newNurse.email,
            phone: newNurse.phone,
        };
        setnurses([...nurses, nurseToAdd]);
        setnewNurse({ name: '', email: '', phone: '' });
    };

    // Fonction pour gérer les changements dans le formulaire
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setnewNurse({ ...newNurse, [name]: value });
    };

    return (
        <div>
            <h1>Gestion de la base de nurses</h1>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nom</th>
                        <th>Email</th>
                        <th>Téléphone</th>
                    </tr>
                </thead>
                <tbody>
                    {nurses.map((nurse) => (
                        <tr key={nurse.id}>
                            <td>{nurse.id}</td>
                            <td>{nurse.name}</td>
                            <td>{nurse.email}</td>
                            <td>{nurse.phone}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Ajouter un nouveau nurse</h2>
            <div>
                <input
                    type="text"
                    name="name"
                    placeholder="Nom"
                    value={newNurse.name}
                    onChange={handleInputChange}
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={newNurse.email}
                    onChange={handleInputChange}
                />
                <input
                    type="text"
                    name="phone"
                    placeholder="Téléphone"
                    value={newNurse.phone}
                    onChange={handleInputChange}
                />
                <button onClick={handleAddnurse}>Ajouter</button>
            </div>
        </div>
    );
}

export default Table;
